package com.ruby.java.ch02;

public class Test27 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//for ë¬¸ 
		for(int i =8 ; i<10 ;i++) {
			System.out.println("for OK");
		}
		//while
		int j = 8;
		while(j<10) {
			System.out.println("while OK");
			j++;
		}
		
		// do while
		int k = 10;
		
		do {
			System.out.println("do-while OK");
			k++;
		} while(k<10);
		
	}

}
